mapper.select:
SELECT A.* FROM (
SELECT MPM.COMPANY_CD , MPM.DIVISION_CD , MPM.PICKING_ID , MPM.PICKING_NM , MPM.STATE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MPM.COMPANY_CD, MPM.DIVISION_CD, 'WM0007', MPM.STATE, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS STATE_NM , MPM.LOCATION_CD AS PICKING_LOCATION_CD , ML.LOCATION_NM AS PICKING_LOCATION_NM , MPM.AREA_CD , (SELECT MOM_COMMON_PKG.FN_GET_AREA_NAME(MPM.COMPANY_CD, MPM.DIVISION_CD, MPM.AREA_CD) FROM DUAL) AS AREA_NM , MPM.PROCESS_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_PROCESS_TYPE_NAME(MPM.COMPANY_CD, MPM.DIVISION_CD, MPM.PROCESS_TYPE) FROM DUAL) AS PROCESS_TYPE_NM , MPM.RECEIPT_TYPE_CD , (SELECT MOM_COMMON_PKG.FN_GET_RECEIPT_TYPE_NAME(MPM.COMPANY_CD, MPM.DIVISION_CD, MPM.RECEIPT_TYPE_CD) FROM DUAL) AS RECEIPT_TYPE_NM , MPM.DESCRIPTION , MPM.UPDATE_BY AS UPDATE_USER_NO , (SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MPM.COMPANY_CD, MPM.DIVISION_CD, MPM.UPDATE_BY) FROM DUAL ) AS UPDATE_USER_NM , TO_CHAR(MPM.UPDATE_DATE ,'YYYY-MM-DD HH24:MI:SS') AS UPDATE_DATE , ROWIDTOCHAR(MPM.ROWID) AS KEY_ID , MPM.PICKING_TYPE , MPM.SEARCH_NM AS RECEIPT_ID , COUNT(*) OVER() AS TOTAL_COUNT , ROW_NUMBER() OVER(PARTITION BY MPM.COMPANY_CD,MPM.DIVISION_CD ORDER BY MPM.PICKING_ID) AS ROW_CNT FROM MOM_LOCATION_PICKING_MST MPM , MOM_LOCATION ML WHERE MPM.COMPANY_CD = ML.COMPANY_CD AND MPM.DIVISION_CD = ML.DIVISION_CD AND MPM.LOCATION_CD = ML.LOCATION_CD AND MPM.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MPM.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} AND MPM.CREATE_DATE BETWEEN TO_DATE(#{createDateSD, jdbcType=VARCHAR}, 'YYYY-MM-DD') AND TO_DATE(#{createDateED, jdbcType=VARCHAR}, 'YYYY-MM-DD') + 0.99999 AND MPM.STATE = 'LPG'
AND MPM.PICKING_TYPE = #{pickingType, jdbcType=VARCHAR}
AND (UPPER(MPM.PICKING_ID) LIKE '%' || UPPER(#{pickingId, jdbcType=VARCHAR}) || '%')
AND MPM.PROCESS_TYPE = #{processType, jdbcType=VARCHAR}
AND MPM.RECEIPT_TYPE_CD = #{receiptTypeCd, jdbcType=VARCHAR}
AND (UPPER(MPM.LOCATION_CD) LIKE '%' || UPPER(#{pickingLocationCdNm, jdbcType=VARCHAR}) || '%' OR UPPER(ML.LOCATION_NM) LIKE '%' || UPPER(#{pickingLocationCdNm, jdbcType=VARCHAR}) || '%')
AND UPPER(MPM.SEARCH_NM) LIKE '%' || UPPER(#{itemReceiptId, jdbcType=VARCHAR})
) A WHERE ROW_CNT BETWEEN #{startPage, jdbcType=INTEGER} AND #{endPage, jdbcType=INTEGER}

mapper.delete:
DELETE FROM MOM_PROC_PICKING_CONFIRM


    (       COMPANY_CD  = #{item.companyCd, jdbcType=VARCHAR}
       AND  DIVISION_CD = #{item.divisionCd, jdbcType=VARCHAR}
       AND  CREATE_BY   = #{item.userId, jdbcType=VARCHAR}
    )

insert.foreach:

        INTO MOM_PROC_PICKING_CONFIRM( COMPANY_CD
		                             , DIVISION_CD
		                             , PICKING_ID
		                             , AREA_CD
		                             , DESCRIPTION
		                             , VAL_MSG
		                             , MULTI_MSG
		                             , CREATE_BY
		                             , CREATE_DATE
		                             )
        VALUES                       ( #{item.companyCd, jdbcType=VARCHAR}
                                     , #{item.divisionCd, jdbcType=VARCHAR}
	                                 , #{item.pickingId, jdbcType=VARCHAR}
	                                 , #{item.areaCd, jdbcType=VARCHAR}
	                                 , #{item.description, jdbcType=VARCHAR}
	                                 , #{item.valMsg, jdbcType=VARCHAR}
	                                 , #{item.multiMsg, jdbcType=VARCHAR}
	                                 , #{item.userId, jdbcType=VARCHAR}
	                                 , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
	                                 )

mapper.select:

        { CALL MOM_WMS_PKG.SP_PICKING_CONFIRM( #{p_err_code,  jdbcType=VARCHAR, mode=OUT}
	                                         , #{p_err_msg,   jdbcType=VARCHAR, mode=OUT}
	                                         , #{companyCd,   jdbcType=VARCHAR, mode=IN}
	                                         , #{divisionCd,  jdbcType=VARCHAR, mode=IN} 
	                                         , #{userId,      jdbcType=VARCHAR, mode=IN} )}
