mapper.select:
SELECT A.* FROM (
SELECT MPD.COMPANY_CD , MPD.DIVISION_CD , MPD.PROCESS_CD , (SELECT MOM_COMMON_PKG.FN_GET_PROCESS_NAME(MPD.COMPANY_CD, MPD.DIVISION_CD, MPD.PROCESS_CD) FROM DUAL) AS PROCESS_NM , MPD.IO_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_IO_TYPE_NAME(MPD.COMPANY_CD, MPD.DIVISION_CD, MPD.IO_TYPE) FROM DUAL) AS IO_TYPE_NM , MPD.REFERENCE_NO , MPD.REFERENCE_DTL_NO , MPD.ITEM_ID , MI.ITEM_NM , MI.ITEM_SPEC , MI.ITEM_TYPE , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MI.COMPANY_CD, MI.DIVISION_CD, 'MD0002', MI.ITEM_TYPE, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS ITEM_TYPE_NM , MPD.FROM_LOCATION_CD AS REQUEST_LOCATION_CD , MLF.LOCATION_NM AS REQUEST_LOCATION_NM , MPD.TO_LOCATION_CD AS ISSUE_LOCATION_CD , MLT.LOCATION_NM AS ISSUE_LOCATION_NM , MPD.QTY AS ISSUE_QTY , MPD.APPLY_FLAG , TO_CHAR(MPD.CREATE_DATE ,'YYYY-MM-DD HH24:MI:SS') AS CREATE_DATE , MPD.CREATE_BY AS REQUEST_USER_NO ,(SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MPD.COMPANY_CD, MPD.DIVISION_CD, MPD.CREATE_BY) FROM DUAL ) AS REQUEST_USER_NM , ROWIDTOCHAR(MPD.ROWID) AS KEY_ID , COUNT(*) OVER() AS TOTAL_COUNT , ROW_NUMBER() OVER(PARTITION BY MPD.COMPANY_CD,MPD.DIVISION_CD ORDER BY MPD.REFERENCE_DTL_NO) AS ROW_CNT FROM MOM_PRODUCT_DELIVERY_SHEET MPD , MOM_ITEM MI , MOM_LOCATION MLF , MOM_LOCATION MLT WHERE MPD.COMPANY_CD = MI.COMPANY_CD AND MPD.DIVISION_CD = MI.DIVISION_CD AND MPD.ITEM_ID = MI.ITEM_ID AND MPD.COMPANY_CD = MLF.COMPANY_CD AND MPD.DIVISION_CD = MLF.DIVISION_CD AND MPD.FROM_LOCATION_CD = MLF.LOCATION_CD AND MPD.COMPANY_CD = MLT.COMPANY_CD AND MPD.DIVISION_CD = MLT.DIVISION_CD AND MPD.TO_LOCATION_CD = MLT.LOCATION_CD AND MPD.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MPD.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} AND MPD.CREATE_DATE BETWEEN TO_DATE(#{createDateSD, jdbcType=VARCHAR}, 'YYYY-MM-DD') AND TO_DATE(#{createDateED, jdbcType=VARCHAR}, 'YYYY-MM-DD') + 0.99999
AND MPD.APPLY_FLAG = #{applyFlag, jdbcType=VARCHAR}
AND MPD.PROCESS_CD = #{processCd, jdbcType=VARCHAR}
AND (UPPER(MPD.FROM_LOCATION_CD) LIKE '%' || UPPER(#{requestLocationCdNm, jdbcType=VARCHAR}) || '%' OR UPPER(MLF.LOCATION_NM) LIKE '%' || UPPER(#{requestLocationCdNm, jdbcType=VARCHAR}) || '%')
AND (UPPER(MPD.TO_LOCATION_CD) LIKE '%' || UPPER(#{issueLocationCdNm, jdbcType=VARCHAR}) || '%' OR UPPER(MLT.LOCATION_NM) LIKE '%' || UPPER(#{issueLocationCdNm, jdbcType=VARCHAR}) || '%')
AND (UPPER(MI.ITEM_ID) LIKE '%' || UPPER(#{itemIdNm, jdbcType=VARCHAR}) || '%' OR UPPER(MI.ITEM_NM) LIKE '%' || UPPER(#{itemIdNm, jdbcType=VARCHAR}) || '%')
AND (UPPER(MPD.REFERENCE_NO) LIKE '%' || UPPER(#{referenceNo, jdbcType=VARCHAR}) || '%' )
AND (UPPER(MPD.REFERENCE_DTL_NO) LIKE '%' || UPPER(#{referenceDtlNo, jdbcType=VARCHAR}) || '%' )
) A WHERE ROW_CNT BETWEEN #{startPage, jdbcType=INTEGER} AND #{endPage, jdbcType=INTEGER}
