mapper.select:
SELECT A.* FROM (
SELECT MLM.COMPANY_CD , MLM.DIVISION_CD , MLM.EXEC_TASK_ID AS UNLOADING_ID , MLM.LOCATION_CD AS UNLOADING_LOCATION_CD , ML.LOCATION_NM AS UNLOADING_LOCATION_NM , MLM.AREA_CD , MA.AREA_NM , MLM.LOGISTICS_UNIT_CD , MLM.RECEIPT_TYPE_CD , MRT.RECEIPT_TYPE_NM , MRT.RECEIPT_STEP_ID , (SELECT MOM_COMMON_PKG.FN_GET_CODE_NAME(MRT.COMPANY_CD, MRT.DIVISION_CD, 'MD0038', MRT.RECEIPT_STEP_ID, #{langCd, jdbcType=VARCHAR}) FROM DUAL) AS RECEIPT_STEP_NM , MLM.DESCRIPTION , MLM.UPDATE_BY AS UPDATE_USER_NO , (SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MLM.COMPANY_CD, MLM.DIVISION_CD, MLM.UPDATE_BY) FROM DUAL ) AS UPDATE_USER_NM , TO_CHAR(MLM.UPDATE_DATE ,'YYYY-MM-DD HH24:MI:SS') AS UPDATE_DATE , MLM.CONFIRM_USER_NO , (SELECT MOM_COMMON_PKG.FN_GET_USER_NAME(MLM.COMPANY_CD, MLM.DIVISION_CD, MLM.CONFIRM_USER_NO) FROM DUAL ) AS CONFIRM_USER_NM , TO_CHAR(MLM.CONFIRM_DATE ,'YYYY-MM-DD') AS CONFIRM_DATE , ROWIDTOCHAR(MLM.ROWID) AS KEY_ID , COUNT(*) OVER() AS TOTAL_COUNT , ROW_NUMBER() OVER(PARTITION BY MLM.COMPANY_CD,MLM.DIVISION_CD ORDER BY MLM.EXEC_TASK_ID) AS ROW_CNT FROM MOM_LOCATION_EXEC_TASK_MST MLM , MOM_LOCATION ML , MOM_AREA MA , MOM_LOCATION_RECEIPT_TYPE MRT WHERE MLM.COMPANY_CD = ML.COMPANY_CD AND MLM.DIVISION_CD = ML.DIVISION_CD AND MLM.LOCATION_CD = ML.LOCATION_CD AND MLM.COMPANY_CD = MA.COMPANY_CD AND MLM.DIVISION_CD = MA.DIVISION_CD AND MLM.AREA_CD = MA.AREA_CD AND MLM.COMPANY_CD = MRT.COMPANY_CD AND MLM.DIVISION_CD = MRT.DIVISION_CD AND MLM.RECEIPT_TYPE_CD = MRT.RECEIPT_TYPE_CD AND MLM.COMPANY_CD = #{companyCd, jdbcType=VARCHAR} AND MLM.DIVISION_CD = #{divisionCd, jdbcType=VARCHAR} AND MLM.CONFIRM_DATE BETWEEN TO_DATE(#{confirmDateSD, jdbcType=VARCHAR}, 'YYYY-MM-DD') AND TO_DATE(#{confirmDateED, jdbcType=VARCHAR}, 'YYYY-MM-DD') + 0.99999 AND MLM.EXEC_TASK_TYPE = 'U' AND MLM.STATE = 'LES'
AND (UPPER(MLM.EXEC_TASK_ID) LIKE '%' || UPPER(#{unloadingId, jdbcType=VARCHAR}) || '%')
AND (UPPER(MLM.LOCATION_CD) LIKE '%' || UPPER(#{unloadingLocationCdNm, jdbcType=VARCHAR}) || '%' OR UPPER(ML.LOCATION_NM) LIKE '%' || UPPER(#{unloadingLocationCdNm, jdbcType=VARCHAR}) || '%')
AND (UPPER(MLM.AREA_CD) LIKE '%' || UPPER(#{areaCdNm, jdbcType=VARCHAR}) || '%' OR UPPER(MA.AREA_NM) LIKE '%' || UPPER(#{areaCdNm, jdbcType=VARCHAR}) || '%')
AND MLM.RECEIPT_TYPE_CD = #{receiptTypeCd, jdbcType=VARCHAR}
) A WHERE ROW_CNT BETWEEN #{startPage, jdbcType=INTEGER} AND #{endPage, jdbcType=INTEGER}

mapper.delete:
DELETE FROM MOM_PROC_LOCATION_EXEC_STATE


        (     COMPANY_CD         = #{item.companyCd, jdbcType=VARCHAR}
         AND  DIVISION_CD        = #{item.divisionCd, jdbcType=VARCHAR}
         AND  EXEC_TASK_TYPE     = 'U'
         AND  ACTION_TYPE        = 'C'
         AND  CREATE_BY          = #{item.userId, jdbcType=VARCHAR}
        )

insert.foreach:

INTO MOM_PROC_LOCATION_EXEC_STATE( COMPANY_CD
                                 , DIVISION_CD
                                 , EXEC_TASK_TYPE
                                 , EXEC_TASK_ID
                                 , LOGISTICS_UNIT_CD
                                 , RECEIPT_STEP_ID
                                 , ACTION_TYPE
                                 , DESCRIPTION
                                 , VAL_MSG
                                 , MULTI_MSG
                                 , CREATE_BY
                                 , CREATE_DATE
                                 )
VALUES                           ( #{item.companyCd, jdbcType=VARCHAR}  
                                 , #{item.divisionCd, jdbcType=VARCHAR}   
                                 , 'U'
                                 , #{item.unloadingId, jdbcType=VARCHAR}
                                 , #{item.logisticsUnitCd, jdbcType=VARCHAR}
                                 , #{item.receiptStepId, jdbcType=VARCHAR}
                                 , 'C'
                                 , #{item.description, jdbcType=VARCHAR}
                                 , #{item.valMsg, jdbcType=VARCHAR}
                                 , #{item.multiMsg, jdbcType=VARCHAR}
                                 , #{item.userId, jdbcType=VARCHAR}
                                 , MOM_COMMON_PKG.FN_GET_LOCAL_TIME(#{item.companyCd, jdbcType=VARCHAR},#{item.divisionCd, jdbcType=VARCHAR})
                                 )

mapper.select:

{ CALL MOM_WMS_PKG.SP_LOCATION_EXEC_STATE_CHANGE( #{p_err_code,  jdbcType=VARCHAR, mode=OUT}
                                                , #{p_err_msg,   jdbcType=VARCHAR, mode=OUT}
                                                , #{companyCd,   jdbcType=VARCHAR, mode=IN}
                                                , #{divisionCd,  jdbcType=VARCHAR, mode=IN}    
                                                , 'U'
                                                , 'C'
                                                , #{userId,      jdbcType=VARCHAR, mode=IN} )}

